definition.script:
node('vm'){

    stage('Run Docker Container'){
        sh 'sudo docker run --name mv -d 10.150.19.24:5000/mjs:latest'

    }
    stage('Git Clone')
    {
        sh 'sudo docker exec -i mv git clone https://github.com/kartikeya-b/maven-sonar.git'
    }
    stage('Build'){
        sh 'sudo docker exec -i mv /bin/sh -c \'cd maven-sonar;mvn clean install\''

    }
    stage('SonarQube Analysis')
    {
       sh 'sudo docker exec -i mv /bin/sh -c \'cd maven-sonar;mvn sonar:sonar -Dsonar.projectKey=org.sonarqube:java-simple-sq-scanner -Dsonar.projectName=Java -Dsonar.login=admin -Dsonar.password=admin -Dsonar.projectVersion=1.0 -Dsonar.language=java -Dsonar.host.url=http://172.16.3.2:9056\''
    }
    stage('War Upload To Nexus')
    {
        sh 'sudo docker exec -i mv /bin/sh -c \'cd maven-sonar;curl -u admin:admin123 -X PUT http://172.16.3.2:9053/nexus/content/repositories/releases/maven-sonar.war -T target/example-java-maven-1.0-SNAPSHOT.jar\''
    }
    stage('Clean Contaier')
    {
        sh 'sudo docker stop mv'
        sh 'sudo docker rm mv'
    }

}
